create table tb_product(id int auto_increment primary key , created_at datetime  not null,  created_by varchar(32)  not null,updated_at datetime  not null,  updated_by varchar(32)  not null,  name varchar(32) not null , type int not null , version int not null);
create table tb_user(id int auto_increment primary key , created_at datetime  not null,  created_by varchar(32)  not null,updated_at datetime  not null,  updated_by varchar(32)  not null,  name varchar(32) not null , address varchar(128) not null ,mobile varchar(32) not null, version int not null);
create table tb_trade_record(id int auto_increment primary key , created_at datetime  not null,  created_by varchar(32)  not null,updated_at datetime  not null,  updated_by varchar(32)  not null,  user_id int not null , product_id int not null , amount decimal not null, version int not null);